<?php namespace App\Http\Controllers; use Illuminate\Http\Request; use App\comments; use App\posts; use App\pages; use App\User; use Carbon; class DashboardController extends Controller {public function __construct() {$this->middleware('auth'); } public function index(){$comments = comments::all(); $posts = posts::all(); $user = User::all(); $pages = pages::all(); return view('dashboard',['comments'=>$comments,'posts'=>$posts,'pages'=>$pages,'user'=>$user]); } }