ngurusCamat; } public function Konflik(Request $request) {$konfliks = konfliks::with('kecamatan')->get(); $view = View::make('laporan.LapKonflik', array('konfliks'=>$konfliks, 'i' => 0))->render(); $pdf = App::make('dompdf.wrapper'); $pdf->loadHTML($view)->setPaper('a4'); return $pdf->stream(); } public function Asing(Request $request) {$asings = asings::with('kecamatan')->get(); $view = View::make('laporan.LapAsing', array('asings'=>$asings, 'i' => 0))->render(); $pdf = App::make('dompdf.wrapper');  $pdf->loadHTML($view)->setPaper('a4','landscape'); return $pdf->stream(); } public function Tomas(Request $request) {$tomas = tomas::with('kecamatan')->get(); $view = View::make('laporan.LapTomas', array('tomass'=>$tomas, 'i' => 0))->render(); $pdf = App::make('dompdf.wrapper'); $pdf->loadHTML($view)->setPaper('a4'); return $pdf->stream(); } public function Todat(Request $request) {$todats = todats::with('kecamatan')->get(); $view = View::make('laporan.LapTodat', array('todats'=>$todats, 'i' => 0))->render();  $pdf = App::make('dompdf.wrapper'); $pdf->loadHTML($view)->setPaper('a4'); return $pdf->stream(); } public function Toga(Request $request) {$togas = togas::with('kecamatan')->get(); $view = View::make('laporan.LapToga', array('togas'=>$togas, 'i' => 0))->render();  $pdf = App::make('dompdf.wrapper');  $pdf->loadHTML($view)->setPaper('a4'); return $pdf->stream(); } public function Toda(Request $request) {$todas = todas::with('kecamatan')->get(); $view = View::make('laporan.LapToda', array('todas'=>$todas, 'i' => 0))->render(); $pdf = App::make('dompdf.wrapper'); $pdf->loadHTML($view)->setPaper('a4');  return $pdf->stream(); } public function Rumah(Request $request) {$rumahs = rumahs::with('kecamatan')->get();  $view = View::make('laporan.LapRumah', array('rumahs'=>$rumahs, 'i' => 0))->render(); $pdf = App::make('dompdf.wrapper'); $pdf->loadHTML($view)->setPaper('a4'); return $pdf->stream(); } }