<?php namespace App\Http\Controllers; use Illuminate\Http\Request; use App\navbars; class navbarfooter extends Controller {public function __construct() {$this->middleware('auth'); } public function tampil() {$navbars = navbars::where('tipe',2)->get(); $parent = navbars::where(['parent'=>null,'tipe'=>2])->get(); return view('navbar-footer',['navbars'=>$navbars,'parent'=>$parent]); } public function tambah(Request $request) {$navbars = new navbars; $navbars->text = $request->text; $navbars->link = $request->link; $navbars->tipe = $request->tipe; if($request->parent!=null){$navbars->parent = $request->parent; } $navbars->save(); return redirect()->action('navbarfooter@tampil'); } public function ubah(Request $request) {$navbars = navbars::find($request->id); $navbars->text = $request->text; $navbars->save(); return redirect()->action('navbarfooter@tampil');    } public function hapus(Request $request) {$navbars = navbars::find($request->id); $navbars->delete(); return redirect()->action('navbarfooter@tampil'); } }