<?php namespace App\Http\Controllers; use Illuminate\Http\Request; use App\sidebars; class sidebarController extends Controller {public function __construct() {$this->middleware('auth'); } public function tampil() {$sidebars = sidebars::all(); return view('sidebar',['sidebars'=>$sidebars]); } public function tambah(Request $request) {$sidebars = new sidebars; $sidebars->judul = $request->judul; $sidebars->html = $request->html; $sidebars->save(); return redirect()->action('sidebarController@tampil'); } public function ubah(Request $request) {$sidebars = sidebars::find($request->id); $sidebars->judul = $request->judul; $sidebars->html = $request->html; $sidebars->save(); return redirect()->action('sidebarController@tampil');    } public function hapus(Request $request) {$sidebars = sidebars::find($request->id); $sidebars->delete(); return redirect()->action('sidebarController@tampil'); } }