<?php namespace App\Http\Controllers; use Illuminate\Http\Request; use App\todas; use App\kecamatans; class todaController extends Controller {public function tampil() {$todas = todas::with('kecamatan')->get(); $kecamatans = kecamatans::all(); return view('kecamatan.toda',['todas'=>$todas,'kecamatans'=>$kecamatans]); } public function tambah(Request $request) {$toda = new todas; $toda->id_kecamatan = $request->id_kecamatan; $toda->nama = $request->nama; $toda->no_hp = $request->no_hp; $toda->alamat = $request->alamat; $toda->save(); return redirect()->action('todaController@tampil'); } public function ubah(Request $request) {$toda = todas::find($request->id); $toda->id_kecamatan = $request->id_kecamatan; $toda->nama = $request->nama; $toda->no_hp = $request->no_hp; $toda->alamat = $request->alamat; $toda->save(); return redirect()->action('todaController@tampil'); } public function hapus(Request $request) {$toda = todas::find($request->id); $toda->delete(); return redirect()->action('todaController@tampil'); } }